function leapNum(y){
  if(y<0)
    return leapNumber[y+849];
  else
    return leapNumber[y-1+849];
}

function leapMon(y){
  var starY=-849;  //记录从公元前850年开始
  var leapM='';//-849-2100
  var v=leapM.charAt(y-starY);
  
  if(v=='a')
    v=10;
  if(v=='b')
    v=11;
  if(v=='c')
    v=12;
  return parseInt(v);
}
